+int acpi_checksum ( struct ACPI_SDTHeader *sdth)
+  {
+  uint8 s=0;
+  uint8 *p;
+
+  for (p=(uint8 *)sdth;p<(uint8 *)(sdth+sdth->length);p++) s += *p;
+  return (int)s;
+  }
