+static int
+acpi_sprint_pnp(char *buffer, int buffer_size, const struct acpi_op *op)
+{
+	char name[5];
+	u32 identifier = 0;
+
+	// This might be a PnP identifier...
+	if (op->parent &&
+	    op->parent->opcode == AML_NAME &&
+	    acpi_sprint_name(name, 5,
+			     acpi_get_name(op->parent)) &&
+	    (!strncmp(name, "_HID", 4) ||
+	     !strncmp(name, "_CID", 4)))
+	{
+		// Parent is a Name named _[HC]ID; this
+		// must be a PnP identifier
+
+		// Battle with endianness :-(
+		switch (op->opcode)
+		{
+		case AML_DWORDCONST:
+			identifier |= (op->value.integer & 0xff0000) >> 8;
+			identifier |= (op->value.integer & 0xff000000) >> 24;
+			// fall through
+		case AML_WORDCONST:
+			identifier |= (op->value.integer & 0xff) << 24;
+			identifier |= (op->value.integer & 0xff00) << 8;
+		}
+
+		return acpi_safe_sprintf(buffer,
+			 buffer_size,
+			 18,
+			 "%c%c%c%04x (0x%08x)",
+			 ((identifier >> 26) & 0x1f) + 0x40,
+			 ((identifier >> 21) & 0x1f) + 0x40,
+			 ((identifier >> 16) & 0x1f) + 0x40,
+			 identifier & 0xffff,
+			 op->value.integer);
+	}
+	else
+	{
+		return 0;
+	}
+}
+
 /*
