+static volatile pid_t reader_pid = -1;
+
+static void
+child_handler (int i) {
+	int status;
+	if (reader_pid != -1 && wait(&status) == reader_pid) {
+		reader_pid = -1;
+		if (WEXITSTATUS(status) != 0)
+			exit(WEXITSTATUS(status));
+	}
+}
+
+static void
+child_cleanup (void) {
+	/* we are too lazy to clean up; child_handler will do it for us	*
+	 * if we wait							*/
+	while (reader_pid != -1)
+		wait(NULL);
+}
+
+int
+use_reader_prog (const char *s) {
+	int fd[2]; 
+	struct sigaction sa = {
+		.sa_handler = child_handler,
+		.sa_flags = SA_NOCLDSTOP
+	};
+
+	if (pipe(fd) == -1) {
+		perror("pipe");
+		exit(EX_SYSERR);
+	}
+	if (sigaction(SIGCHLD, &sa, NULL) == -1) {
+		perror("sigaction");
+		exit(EX_SYSERR);
+	}
+	if ((reader_pid = fork()) == -1) {
+		perror("fork");
+		exit(EX_SYSERR);
+	} else if (reader_pid) {	/* parent */
+		atexit(child_cleanup);
+		close(fd[1]);
+		return fd[0];
+	} else {		/* child */
+		close(fd[0]);
+		if (dup2(fd[1], STDOUT_FILENO) == -1) {
+			perror("dup2");
+			exit(EX_SYSERR);
+		}
+		setuid(getuid());	/* set euid to ruid */
+		if (execlp(s, s, NULL) == -1) {
+			perror(s);
+			exit(EX_USAGE);
+		}
+	}
+
+	return 0; /* so gcc will shut up */
