["loop-reset-size.c" (text/x-csrc)]


#include <sys/ioctl.h>
#include <sys/types.h>
#include <sys/stat.h>
#include <fcntl.h>

#include <stdio.h>

#define LOOP_RESET_SIZE 0x4C07

int main(int argc, char **argv) 
{
	int fd, result;

	fd = open(argv[1], O_RDONLY);
	result = ioctl(fd, LOOP_RESET_SIZE);

	printf("result: %d\n", result);
}

