> +	.cra_u			=	{
> +		.cipher = {
> +			.cia_min_keysize	=  AES_KEY_LENGTH,
> +			.cia_max_keysize	=  AES_KEY_LENGTH,
> +			.cia_setkey		=  geode_aes_setkey,
> +			.cia_encrypt		=  geode_aes_encrypt,
