@@ -0,0 +1,117 @@
+/*
+ * Create default crypto algorithm instances.
+ *
+ * Copyright (c) 2006 Herbert Xu <herbert@gondor.apana.org.au>
+ *
+ * This program is free software; you can redistribute it and/or modify it
+ * under the terms of the GNU General Public License as published by the Free
+ * Software Foundation; either version 2 of the License, or (at your option)
+ * any later version.
+ *
+ */
+
+#include <linux/crypto.h>
+#include <linux/ctype.h>
+#include <linux/err.h>
+#include <linux/init.h>
+#include <linux/module.h>
+#include <linux/notifier.h>
+#include <linux/rtnetlink.h>
+#include <linux/string.h>
+
+#include "internal.h"
+
+struct cryptomgr_param {
+	struct {
+		struct rtattr attr;
+		struct crypto_attr_alg data;
+	} alg;
+};
+
+static int cryptomgr_probe(struct crypto_larval *larval)
+{
+	struct cryptomgr_param param;
+	struct crypto_template *tmpl;
+	struct crypto_instance *inst;
+	const char *name = larval->alg.cra_name;
+	const char *p;
+	unsigned int len;
+
+	for (p = name; isalnum(*p) || *p == '-' || *p == '_'; p++)
+		;
+
+	len = p - name;
+	if (!len || *p != '(')
+		return NOTIFY_OK;
+
+	memcpy(param.alg.data.name, name, len);
+	param.alg.data.name[len] = 0;
+
+	name = p + 1;
+	for (p = name; isalnum(*p) || *p == '-' || *p == '_'; p++)
+		;
+
+	len = p - name;
+	if (!len || *p != ')' || p[1])
+		return NOTIFY_OK;
+
+	tmpl = crypto_lookup_template(param.alg.data.name);
+	if (!tmpl)
+		goto err;
+
+	param.alg.attr.rta_len = sizeof(param.alg);
+	param.alg.attr.rta_type = CRYPTOA_ALG;
+	memcpy(param.alg.data.name, name, len);
+	param.alg.data.name[len] = 0;
+
+	inst = tmpl->alloc(&param, sizeof(param));
+	if (IS_ERR(inst))
+		inst = NULL;
+	else if (crypto_register_instance(tmpl, inst)) {
+		tmpl->free(inst);
+		inst = NULL;
+	}
+
+	crypto_tmpl_put(tmpl);
+
+	if (!inst)
+		goto err;
+
+	return NOTIFY_STOP;
+
+err:
+	crypto_larval_error(larval->alg.cra_name);
+	return NOTIFY_STOP;
+}
+
+static int cryptomgr_notify(struct notifier_block *this, unsigned long msg,
+			    void *data)
+{
+	switch (msg) {
+	case CRYPTO_MSG_ALG_REQUEST:
+		return cryptomgr_probe(data);
+	}
+
+	return NOTIFY_DONE;
+}
+
+static struct notifier_block cryptomgr_notifier = {
+	.notifier_call = cryptomgr_notify,
+};
+
+static int __init cryptomgr_init(void)
+{
+	return crypto_register_notifier(&cryptomgr_notifier);
+}
+
+static void __exit cryptomgr_exit(void)
+{
+	int err = crypto_unregister_notifier(&cryptomgr_notifier);
+	BUG_ON(err);
+}
+
+module_init(cryptomgr_init);
+module_exit(cryptomgr_exit);
+
+MODULE_LICENSE("GPL");
+MODULE_DESCRIPTION("Crypto Algorithm Manager");
