+static int crypto_remove_alg(struct crypto_alg *alg, struct list_head *list)
+{
+	struct crypto_spawn *spawn, *n;
+	LIST_HEAD(spawns);
+
+	if (unlikely(list_empty(&alg->cra_list)))
+		return -ENOENT;
+
