+static enum alg_ids name_to_id(const char *name)
+{
+	struct name_id_map *algo = map;
+
+	if (!name)
+		return ALG_NONE;
+
+	while (algo->name) {
+		if (strcmp(algo->name, name) == 0)
+			return algo->id;
+		algo++;
+	}
+
+	printk("Unknown algorithm '%s'.\n", name);
+	return ALG_NONE;
+}
+
+#if 0 /* not used */
+static const char *id_to_name(enum alg_ids id)
+{
+	struct name_id_map *algo = map;
+	while (algo->name) {
+		if (algo->id == id)
+			return algo->name;
+		algo++;
+	}
+	return NULL;
+}
+#endif
