+int is_loop_in_use(const char *dev)
+{
+	int fd;
+	int ret = 0;
+	struct stat statbuf;
+	if (stat (dev, &statbuf) == 0 && S_ISBLK(statbuf.st_mode)) {
+		fd = open (dev, O_RDONLY);
+		if (fd >= 0) {
+			if (is_unused_loop_device(fd) == 0)
+				ret = 1;  /* in use */
+			close(fd);
+		}
+	}
+	return ret;
+}
