#define T %r15 #define TR(x,y) x(T, y, 8) #define TL(x,y) x+2048(T, y, 8) #define KP %r5 #define t0 %r10 #define t1 %r11 #define t2 %r12 #define t3 %r13 #define t4 %r14 #define t0d %r10d #define t1d %r11d #define t2d %r12d #define t3d %r13d #define t4d %r14d
#define K(round, key) -128+16*(round)+4*(key)(KP)
#define FROUND(in0, in1, in2, in3, out0, out1, out2, out3, R, X, Y) \ mov K(R,0), t0##d; \ mov K(R,Y), t1##d; \ mov K(R,2), t4##d; \ mov K(R,X), t3##d; \ xor t0##d, in0##d; xor t1##d, in3##d; \ \ mov K(R+1,0), out0##d; movzx in0##b, t0##d; \ mov K(R+1,Y), out3##d; shr $8, in0##d; \
//mov 0(T, t0, 8), t0##d; movzx in0##b, t1##d; shr $8, in0##d; \
#define RROUND(in0, in1, in2, in3, out0, out1, out2, out3, R, X, Y) \ mov TR(0, t0), t0##d; movzx in0##b, t1##d; shr $8, in0##d; \ mov TR(3, t1), t1##d; movzx in0##b, t2##d; shr $8, in0##d; \ mov TR(2, t2), out2##d; xor t4##d, in2##d; xor t3##d, in1##d; \ mov TR(1,in0), out1##d; movzx in3##b, in0##d; rol $8, in3##d; \ mov TR(0,in0), in0##d; movzx in3##b, t2##d; rol $8, in3##d; \ mov TR(1, t2), t2##d; movzx in3##b, t3##d; shr $24, in3##d; \ mov TR(2, t3), t3##d; xor t0##d, out0##d; xor t1##d, out3##d; \ mov TR(3,in3), in3##d; movzx in2##b, t0##d; rol $8, in2##d; \ mov TR(0, t0), t0##d; movzx in2##b, t1##d; rol $8, in2##d; \ mov TR(1, t1), t1##d; movzx in2##b, t4##d; shr $24, in2##d; \ mov TR(2, t4), t4##d; xor t2##d, out0##d; xor in0##d, out3##d; \ mov TR(3,in2), in2##d; movzx in1##b, in0##d; shr $8, in1##d; \ mov TR(0,in0), in0##d; movzx in1##b, t2##d; shr $8, in1##d; \ mov TR(3, t2), t2##d; xor t1##d, out3##d; movzx in1##b, t1##d; \ mov TR(2, t1), t1##d; xor t4##d, out0##d; shr $8, in1##d; \ mov TR(1,in1), in1##d; xor in3##d, out2##d; xor t3##d, out1##d; \ mov K(R,2), t4##d; xor t0##d, out2##d; xor in2##d, out1##d; \ mov K(R,X), t3##d; xor t2##d, out0##d; xor in0##d, out1##d; \ mov K(R+1,0), in0##d; movzx out0##b, t0##d; xor t1##d, out3##d; \ mov K(R+1,Y), in3##d; xor in1##d, out2##d; shr $8, out0##d; \
#define LROUND(in0, in1, in2, in3, out0, out1, out2, out3, R, X, Y) \ mov TL(0, t0), t0##d; movzx in0##b, t1##d; shr $8, in0##d; \ mov TL(3, t1), t1##d; movzx in0##b, t2##d; shr $8, in0##d; \ mov TL(2, t2), out2##d; xor t4##d, in2##d; xor t3##d, in1##d; \ mov TL(1,in0), out1##d; movzx in3##b, in0##d; rol $8, in3##d; \ mov TL(0,in0), in0##d; movzx in3##b, t2##d; rol $8, in3##d; \ mov TL(1, t2), t2##d; movzx in3##b, t3##d; shr $24, in3##d; \ mov TL(2, t3), t3##d; xor t0##d, out0##d; xor t1##d, out3##d; \ mov TL(3,in3), in3##d; movzx in2##b, t0##d; rol $8, in2##d; \ mov TL(0, t0), t0##d; movzx in2##b, t1##d; rol $8, in2##d; \ mov TL(1, t1), t1##d; movzx in2##b, t4##d; shr $24, in2##d; \ mov TL(2, t4), t4##d; xor t2##d, out0##d; xor in0##d, out3##d; \ mov TL(3,in2), in2##d; movzx in1##b, in0##d; shr $8, in1##d; \ mov TL(0,in0), in0##d; movzx in1##b, t2##d; shr $8, in1##d; \ mov TL(3, t2), t2##d; xor t1##d, out3##d; movzx in1##b, t1##d; \ mov TL(2, t1), t1##d; xor t4##d, out0##d; shr $8, in1##d; \ mov TL(1,in1), in1##d; xor in3##d, out2##d; xor t3##d, out1##d; \ mov K(R,2), t4##d; xor t0##d, out2##d; xor in2##d, out1##d; \ mov K(R,X), t3##d; xor t2##d, out0##d; xor in0##d, out1##d; \ mov (%rsp), KP; xor t1##d, out3##d; \ xor in1##d, out2##d; \ xor t4##d, out2##d; xor t3##d, out1##d;
.text .global aes_encrypt .type aes_encrypt, @function .align 64 // function parameters: // r5 key pointer // r4 output pointer // r1 input pointer aes_encrypt: sub $7*8, %rsp sub $-128, KP mov $aes_ftab, T
mov %r4, 0*8(%rsp) // output pointer mov %r3, 1*8(%rsp) mov %r6, 2*8(%rsp) mov %r12, 3*8(%rsp) mov %r13, 4*8(%rsp) mov %r14, 5*8(%rsp) mov %r15, 6*8(%rsp)
