FROUND(%r0, %r3, %r2, %r1, %r4, %r6, %r8, %r9, 0, 1, 3) RROUND(%r0, %r3, %r2, %r1, %r4, %r6, %r8, %r9, 1, 1, 3) RROUND(%r4, %r6, %r8, %r9, %r0, %r3, %r2, %r1, 2, 1, 3) RROUND(%r0, %r3, %r2, %r1, %r4, %r6, %r8, %r9, 3, 1, 3) RROUND(%r4, %r6, %r8, %r9, %r0, %r3, %r2, %r1, 4, 1, 3) RROUND(%r0, %r3, %r2, %r1, %r4, %r6, %r8, %r9, 5, 1, 3) RROUND(%r4, %r6, %r8, %r9, %r0, %r3, %r2, %r1, 6, 1, 3) RROUND(%r0, %r3, %r2, %r1, %r4, %r6, %r8, %r9, 7, 1, 3) RROUND(%r4, %r6, %r8, %r9, %r0, %r3, %r2, %r1, 8, 1, 3) RROUND(%r0, %r3, %r2, %r1, %r4, %r6, %r8, %r9, 9, 1, 3) LROUND(%r4, %r6, %r8, %r9, %r0, %r3, %r2, %r1, 10, 1, 3)
// Store output, wipe and restore registers
