addq $7*8, %rsp ret .size aes_encrypt, .-aes_encrypt
.global aes_decrypt .type aes_decrypt, @function .align 64 // function parameters: // r5 key pointer // r4 output pointer // r1 input pointer aes_decrypt: sub $7*8, %rsp sub $-128-240, KP // decryption key expansion is offset by 240 bytes mov $aes_itab, T
