#ifndef PERSON_H
#define PARSON_H
#include <iostream.h>
using namespace std;

struct Name
{
        char firstname[80];
        char surname[80];

        void show()
        {
                cout << firstname << " " << surname;
        }
};

struct Date
{
        int day;
        int month;
        int year;

        void show()
        {
                cout << month << "/" << day << "/" << year;
        }
};

struct Phone
{
        int areacode;
        int number;

        void show()
        {
                cout << areacode << " " << number;
        }
};

struct Person
{
        Name name;
        Date birthdate;
        Phone number;

        void show()
        {
                cout << endl;
                name.show();
                cout << endl;
                cout << "Born: ";
                birthdate.show();
                cout << endl;
                cout << "Telephone: ";
                number.show();
                cout << endl;
        }

        int age(Date& date)
        {
                if(date.year <= birthdate.year)
                        return 0;

                int years = date.year - birthdate.year;

                if((date.month > birthdate.month) || (date.month ==
birthdate.m
                        return years;
                else
                        return --years;
        }
};
#endif

