 $toolbar = &new GtkToolbar( GTK_ORIENTATION_HORIZONTAL, GTK_TOOLBAR_BOTH);
//$toolbar = &new GtkToolbar( GTK_ORIENTATION_HORIZONTAL, GTK_TOOLBAR_TEXT);
//$toolbar = &new GtkToolbar( GTK_ORIENTATION_HORIZONTAL, GTK_TOOLBAR_ICONS);

function toolbarclicked($button, $toolbar)
{
	$toolbar->set_orientation(GTK_ORIENTATION_VERTICAL);
	$toolbar->set_style(GTK_TOOLBAR_ICONS);
}

$toolbar->append_item( 'new', 'Creates a new document', null, $pixmapwid, \
'toolbarClicked', $toolbar ); $toolbar->append_item( 'old', 'Creates a new document', \
null, null, 'toolbarClicked', $toolbar );

$window->add( $toolbar);
$window->show_all();
                                                                                      \
 gtk::main();


?>



