["cursor.php.txt" (text/plain)]
<?php if( !extension_loaded('gtk')) { dl( 'php_gtk.' . PHP_SHLIB_SUFFIX); }
$window = &new GtkWindow(); $window->set_default_size(300, 300); $window->connect_object('destroy', array('gtk', 'main_quit'));
$cursorpmp = array( "5 5 2 1", "0 m white", "1 m black", "11111", "10000", "10000", "10000", "10000" );
$window->realize(); $gdkwin = $window->window;
list($pixmap, $mask) = gdk::pixmap_create_from_xpm_d($gdkwin, null, $cursorpmp);
$style = new GtkStyle(); $cursor =gdk::cursor_new_from_pixmap($pixmap, $mask, $style->white, $style->black, 1, 1);
$gdkwin->set_cursor($cursor); $window->show();
gtk::main(); ?>
