["working_treeview.php.txt" (text/plain)]

<?php

$window = new GtkWindow();
$window->set_title('Study TreeView');
$window->set_position(Gtk::WIN_POS_CENTER);
$window->set_default_size(200, 300);
$window->set_border_width(0);
$window->connect_object('destroy', array('gtk', 'main_quit'));

function callback($store, $path, $iter, $data = null) {

	$i = $path[0]; // path string
	$store->set($iter, 0, "iter_test_$i", 1, "iter_test_$i");
}

function create_Model(){

	$store = new GtkListStore(GTK::TYPE_STRING, GTK::TYPE_STRING);

	$store->append(array('test1','test1'));
	$store->append(array('test2','test2'));
	$store->append(array('test3','test3'));

	$store->foreach('callback');
	return $store;
}

function Create_TreeView(){

	$model = Create_Model();
	$tree = new GtkTreeView($model);

	$column = new GtkTreeViewColumn();
	$column->set_title('Column #1');
	$cell_renderer = new GtkCellRendererText();
	$column->pack_start($cell_renderer, true);
	$column->set_attributes($cell_renderer, 'text', 0);
	$tree->insert_column($column, 0);

	$column = new GtkTreeViewColumn();
	$column->set_title('Column #2');
	$cell_renderer = new GtkCellRendererText();
	$column->pack_start($cell_renderer, true);
	$column->set_attributes($cell_renderer, 'text', 1);
	$tree->insert_column($column, 1);

	return $tree;
}

$hbox = new GtkHBox(true, 10);
$hbox->pack_start(Create_TreeView());
$window->add($hbox);
$window->show_all();

gtk::main();

?>

