ZEND_BEGIN_ARG_INFO(ARGINFO_NAME, 0)
    ZEND_ARG_INFO(0, types)
ZEND_END_ARG_INFO();

%%
override gtk_ui_manager_get_toplevels
static PHP_METHOD(GtkUIManager, get_toplevels)
{
  long types;
  GSList *toplevels = 0;

  if (!php_gtk_parse_args(ZEND_NUM_ARGS(), "i", &types))
    return;

  array_init(return_value);

  for (toplevels = gtk_ui_manager_get_toplevels(GTK_UI_MANAGER(PHPG_GOBJECT(this_ptr)), \
types); toplevels; toplevels = toplevels->next) {  zval *item = NULL;
      const-char *name = NULL;
      name = gtk_widget_get_name(toplevels->data);
      phpg_gobject_new(&item, G_OBJECT(toplevels->data) TSRMLS_CC);
        add_assoc_zval(return_value, name, item);
  }

  g_slist_free(toplevels);
}

%%
override gtk_ui_manager_get_action_groups 
static PHP_METHOD(GtkUIManager, get_action_groups)
{
  GList *action_groups = 0;

  if (!php_gtk_parse_args(ZEND_NUM_ARGS(), ""))
    return;

  array_init(return_value);

  for (action_groups = \
gtk_ui_manager_get_action_groups(GTK_UI_MANAGER(PHPG_GOBJECT(this_ptr))); \
action_groups; action_groups = action_groups->next) {  zval *item = NULL;
      const-char *name = NULL;
      name = gtk_action_group_get_name(action_groups->data);
      phpg_gobject_new(&item, G_OBJECT(action_groups->data) TSRMLS_CC);
      add_assoc_zval(return_value, name, item);
  }
}

