<?php
if( !class_exists('gtk')) {
  die('Please load the php-gtk2 module in your php.ini' . "\r\n");
}
function EndDialog($button, $fs) {
  print "OK button clicked, ";
  $path = $fs->get_filename();
  print "$path selected.\n";
  $GLOBALS['acgroups']['EditActions']->set_sensitive(1);

  return true;
}

function CancelDialog() {
  print("Cancel button clicked\n");
  return true;
}

function FileDialog() {
  $fs = &new GtkFileSelection('File Selection Example');
  $ok_button = $fs->ok_button;
  $ok_button->connect('clicked', 'EndDialog', $fs);
  $ok_button->connect_simple('clicked', array($fs, 'destroy'));
  $cancel_button = $fs->cancel_button;
  $cancel_button->connect('clicked', 'CancelDialog');
  $cancel_button->connect_simple('clicked', array($fs, 'destroy'));
  $fs->show();
}

function FileClose(){
  $GLOBALS['acgroups']['EditActions']->set_sensitive(0);
}
function shutdown() {
  Gtk::main_quit();
}
function canClose() {
  return false;
}
$layout = '
<ui>
  <menubar name="menubar1">
    <menu action="FileMenu">
      <menuitem action="Open"/>
      <menuitem action="Close"/>
      <separator/>
      <menuitem action="Exit"/>
    </menu>
    <menu action="EditMenu">
      <menuitem action="Copy"/>
      <menuitem action="Paste"/>
    </menu>
  </menubar>
  <toolbar action="toolbar1">
    <placeholder name="JustifyToolItems">
      <separator/>
        <toolitem action="Open"/>
        <toolitem action="Close"/>
      <separator/>
    </placeholder>
  </toolbar>
</ui>';


