$vbox1->show();
$vbox1->set_spacing(1);

$ui_manager = new GtkUiManager();

$file_action_group = new GtkActionGroup("FileActions");
$ui_manager->insert_action_group($file_action_group, 0);

$fileAction = new GtkAction('FileMenu','_File','File Actions','');
$openAction = new GtkAction('Open','_Open','Open File','GTK_STOCK_OPEN');
$openAction->connect('activate','FileDialog');
$closeAction = new GtkAction('Close','_Close','Close File','GTK_STOCK_CLOSE');
$closeAction->connect('activate','FileClose');
$exitAction = new GtkAction('Exit','E_xit','Quit','GTK_STOCK_QUIT');
$exitAction->connect('activate','shutdown');

$file_action_group->add_action($fileAction);
$file_action_group->add_action($openAction);
$file_action_group->add_action($closeAction);
$file_action_group->add_action($exitAction);

$edit_action_group = new GtkActionGroup("EditActions");
$ui_manager->insert_action_group($edit_action_group, 0);

$editAction = new GtkAction('EditMenu','_Edit','Edit Actions','');
$copyAction = new GtkAction('Copy','_Copy','Copy to clipboard','GTK_STOCK_COPY');
$pasteAction = new GtkAction('Paste','_Paste','Paste from clipboard','GTK_STOCK_PASTE');

$edit_action_group->add_action($editAction);
$edit_action_group->add_action($copyAction);
$edit_action_group->add_action($pasteAction);

$ui_manager->add_ui_from_string($layout);
$ui_manager->ensure_update();

$toplevels = $ui_manager->get_toplevels(5);
//print_r($toplevels);

$vbox1->pack_start($toplevels['menubar1'],FALSE,FALSE,0);
$vbox1->pack_start($toplevels['toolbar1'],FALSE,FALSE,0);

$acgroups = $ui_manager->get_action_groups();
//print_r($acgroups);
$acgroups['EditActions']->set_sensitive(0);

$window->add($vbox1);
$window->show_all();
Gtk::main();

