This works for me as a basic test and will need more work :)

static void glade_signal_marshal(const gchar *handler_name, GObject *obj, const gchar \
*signal_name, const gchar *signal_data, GObject *connect_object, gboolean after, \
gpointer data) {
    phpg_cb_data_t *cbd = (phpg_cb_data_t *)data;
    char *callback_name;
    guint signal_id, handler_id;
    GQuark detail = 0;
    GClosure *closure = NULL;
    TSRMLS_FETCH();

    fprintf(stdout, "DEBUG: reached the marshal function %s : \
%s\n",handler_name,signal_name); // I never get this far - why?

    if (!zend_is_callable(cbd->callback, 0, &callback_name)) {
        php_error(E_WARNING, "unable to call GladeXML callback '%s' specified in %s \
on line %ld",  callback_name, cbd->src_filename, cbd->src_lineno);
        efree(callback_name);
        return;
    }

    /* If we ever reach here, this is where cbd->user_args should be checked... */
    if (!g_signal_parse_name(signal_name, G_OBJECT_TYPE(obj), &signal_id, &detail, \
                TRUE)) {
      php_error(E_WARNING, "%s(): unknown signal name '%s'", \
get_active_function_name(TSRMLS_C), signal_name);  return;
    }

    closure = phpg_closure_new(cbd->callback, cbd->user_args, PHPG_CONNECT_NORMAL \
TSRMLS_CC);  handler_id = g_signal_connect_closure_by_id(obj, signal_id, detail, \
closure, after);

    efree(callback_name);
}

static PHP_METHOD(GladeXML, signal_connect)
{
    char *signal = NULL;
    zval *callback = NULL, *extra = NULL;
    zend_bool free_signal;
    phpg_cb_data_t *data;

    NOT_STATIC_METHOD();

    if (!php_gtk_parse_varargs(ZEND_NUM_ARGS(), 2, &extra, "uV", &signal, \
&free_signal, &callback))  return;

    zval_add_ref(&callback);
    data = phpg_cb_data_new(callback, extra TSRMLS_CC);

    fprintf(stdout, "signal name is %s on line 175\n", signal);

    glade_xml_signal_connect_full(GLADE_XML(PHPG_GOBJECT(this_ptr)), signal,
                                  (GladeXMLConnectFunc)glade_signal_marshal, data);

    if (free_signal) g_free(signal);

    RETURN_NULL();
}


