+%%
+add-arginfo GtkTextBuffer set_text
+static
+ZEND_BEGIN_ARG_INFO(ARGINFO_NAME, 0)
+    ZEND_ARG_INFO(0, text)
+    ZEND_ARG_INFO(0, len)
+ZEND_END_ARG_INFO();
+
+%%
+override gtk_text_buffer_set_text
+PHP_METHOD
+{
+
+    int len = 0;
+
+    NOT_STATIC_METHOD();
+    if (!php_gtk_parse_args(ZEND_NUM_ARGS(), "u|i", &text, &php_len))
+        return;
+
+    if (!php_len) {
+        len = strlen(text);
+    } else {
+        len = php_len;
+    }
+
+    gtk_text_buffer_set_text(GTK_TEXT_BUFFER(PHPG_GOBJECT(this_ptr)), text, len);
+}
+
 %% }}}



