["phpgtk2-demo.php.txt" (text/plain)]

<?php

/*
* dl() is deprecated in PHP 5 - use php.ini to load PHP-GTK 2
*/

if (!class_exists('gtk')) {
	die("Please load the php-gtk2 module in your php.ini\r\n");
}

class PHPGTK2Demo extends GtkWindow {

	protected $demos = array();
	protected $description_buffer;
	protected $demobox;
	private $highlighting = true;

	static protected $colors = array("blue", "red", "orange", "darkgreen", "black");

	public function __construct() {

		parent::__construct();

		$this->connect_simple('destroy', array('gtk', 'main_quit'));

		$this->set_title('PHP-GTK 2 Demos');
		$this->set_default_size(800, 400);

		$hbox = new GtkHBox(false, 3);
		$this->add($hbox);

		$treeview = $this->__create_treeview();
		$hbox->pack_start($treeview, false, false);

		$notebook = new GtkNotebook();
		$hbox->pack_start($notebook, true);

		$notebook->append_page($this->__create_text(), new GtkLabel('Info'));

		$demobox = new GtkVBox();
		$this->demobox = $demobox;
		$notebook->append_page($this->demobox, new GtkLabel('Demo'));

		list($text, $this->sourcebuffer) = $this->__create_source();
		$notebook->append_page($text, new GtkLabel('Source'));

		$this->show_all();

	}//function __construct()


	function __create_treeview() {

		$this->load_demos();

		$model = new GtkListStore(Gtk::TYPE_PHP_VALUE, Gtk::TYPE_STRING);

		foreach ($this->demos as $demo) {
			$iter = $model->append();
			$model->set($iter, 0, $demo);
		}

		$treeview = new GtkTreeView($model);

		$cell_renderer = new GtkCellRendererText();
		$treeview->insert_column_with_data_func(-1, 'Demos', $cell_renderer, array(&$this, \
'label_setter'));

		$selection = $treeview->get_selection();
		$selection->set_mode(Gtk::SELECTION_SINGLE);
		$selection->connect('changed', array($this, 'demo_selected'));

		return $treeview;

	}//function __create_treeview()


	function __create_source() {

		$scrolled_window = new GtkScrolledWindow();
		$scrolled_window->set_policy(Gtk::POLICY_AUTOMATIC, Gtk::POLICY_AUTOMATIC);
		$scrolled_window->set_shadow_type(Gtk::SHADOW_IN);

		$text_view = new GtkTextView();
		$scrolled_window->add($text_view);

		$buffer = new GtkTextBuffer();
		$text_view->set_buffer($buffer);
		$text_view->set_editable(false);
		$text_view->set_cursor_visible(false);

		$tag_table = $buffer->get_tag_table();

		foreach (self::$colors as $color) {
			$tag = new GtkTextTag($color);
			$tag->set_property("foreground", $color);
			$tag_table->add($tag);
		}

		return array($scrolled_window, $buffer);

	}


	function label_setter($column, $cell, $model, $iter) {

		$info = $model->get_value($iter, 0);
		$cell->set_property('text', $info->classname);

	}


	function demo_selected($selection) {

		list($model, $iter) = $selection->get_selected();

		if (!$iter) {
			return;
		}

		$info = $model->get_value($iter, 0);

		$text = $info->classname . "\r\n\r\n" . $info->description;
		$this->description_buffer->set_text($text);

		//source code highlighting and switch
		$this->highlightSource($info->file, $this->highlighting);

		//remove demobox child
		$children = $this->demobox->get_children();

		if (!empty($children)) {
			$this->demobox->remove($children[0]);
		}

		$classname = $info->classname;

		if ($classname) {
			$scroll_demo = new GtkScrolledWindow();
			$scroll_demo->set_policy(Gtk::POLICY_AUTOMATIC, Gtk::POLICY_AUTOMATIC);

			$obj = new $classname();
			$subobj = $obj->__create_box();

			$scroll_demo->add_with_viewport($subobj);
			$this->demobox->add($scroll_demo);
			$this->demobox->show_all();
		}
	}


	function __create_text() {

		$scrolled_window = new GtkScrolledWindow();
		$scrolled_window->set_policy(Gtk::POLICY_AUTOMATIC, Gtk::POLICY_AUTOMATIC);
		$scrolled_window->set_shadow_type(Gtk::SHADOW_IN);

		$text_view = new GtkTextView();
		$scrolled_window->add($text_view);

		$this->description_buffer = new GtkTextBuffer();
		$text_view->set_buffer($this->description_buffer);
		$text_view->set_editable(false);
		$text_view->set_cursor_visible(false);
		$text_view->set_wrap_mode(Gtk::WRAP_WORD);

		return $scrolled_window;

	}


	protected function load_demos() {

		define('DIR_SEPARATOR', strstr(PHP_OS, 'WIN') ? '\\' : '/');

		/* keep a list of the files we actually want to load into the
		framework - there are generally more exceptions than rules! */

		$target_files = array('buttonbox', 
							'colorselector', 
							'combobox', 
							'dialogmessage', 
							'entrycompletion', 
							'expander', 
							'gtkiconview', 
							'sizegroup', 
							'stock-browser2',
		);

		$files = glob(dirname(__FILE__).DIR_SEPARATOR.'*.php');

		foreach ($files as $id => $file) {

			$GLOBALS['class'] = null;
			$check = substr($file, strrpos($file, DIR_SEPARATOR) + 1, -4);

			if (in_array($check, $target_files)) {

				if (!@include_once($file)) {
					continue;
				}

				$this->demos[$file]					= null;
				$this->demos[$file]->classname		= $GLOBALS['class'];
				$this->demos[$file]->description	= $GLOBALS['description'];
				$this->demos[$file]->file			= $file;
			}
		}

	}//protected function load_demos()


	protected function highlightSource($filename, $switch) {

		$filecontents = file_get_contents($filename);
		$tokens = token_get_all($filecontents);

		$highlighted = '';
		$color_codes = array();

		if ($switch) {

			// this would be much easier if we had functions which supported varargs (like \
insert_with_tags_by_name), but improvised solution works in the meantime.  
			foreach ($tokens as $token) {

				$start = strlen($highlighted);

				if (is_string($token)) {

					//single string
					if($token == "(" || $token == ")" || $token == "{" || $token == "}" || $token == \
";" || $token == "@" || $token == "[" || $token == "]" || $token == "!") {  $color = \
"darkgreen";  }
					else {
						$color = "black";
					}

					$highlighted .= $token;

				} else {

					list($type, $value) = $token;

					switch($type) {
						case T_COMMENT:
						case T_DOC_COMMENT:
							$color = "orange";
							break;
						case T_CLASS:
						case T_DOUBLE_ARROW:
						case T_NEW:
						case T_PAAMAYIM_NEKUDOTAYIM:
						case T_IF:
						case T_OBJECT_OPERATOR:
						case T_RETURN:
						case T_EXTENDS:
						case T_CLASS_C:
						case T_FUNC_C:
						case T_WHILE:
						case T_EXIT:
						case T_INC:
						case T_IS_EQUAL:
						case T_IS_EQUAL:
						case T_IS_IDENTICAL:
						case T_IS_NOT_EQUAL:
						case T_IS_NOT_IDENTICAL:
						case T_IS_SMALLER_OR_EQUAL:
						case T_MINUS_EQUAL:
						case T_MOD_EQUAL:
						case T_MUL_EQUAL:
						case T_OR_EQUAL:
						case T_PLUS_EQUAL:
						case T_FOREACH:
						case T_AS:
						case T_FOR:
						case T_DO:
						case T_CONTINUE:
						case T_BREAK:
						case T_SWITCH:
						case T_CASE:
						case T_LIST:
						case T_PRINT:
						case T_ECHO:
						case T_ARRAY_CAST:
						case T_BOOL_CAST:
						case T_DOUBLE_CAST:
						case T_INT_CAST:
						case T_OBJECT_CAST:
						case T_STRING_CAST:
						case T_UNSET_CAST:
						case T_UNSET:
						case T_INCLUDE:
						case T_INCLUDE_ONCE:
						case T_REQUIRE:
						case T_REQUIRE_ONCE:
						case T_ELSE:
						case T_ELSEIF:
						case T_GLOBAL:
						case T_ARRAY:
						case T_BOOLEAN_AND:
						case T_BOOLEAN_OR:
						case T_CONCAT_EQUAL:
						case T_DIV_EQUAL:
						case T_CONST:
						case T_STATIC:
						case T_AND_EQUAL:
						case T_FUNCTION:
							$color = "darkgreen";
							break;
						case T_LNUMBER:
						case T_CLONE:
						case T_OPEN_TAG:
						case T_CLOSE_TAG:
						case T_NUM_STRING:
						case T_DNUMBER:
						case T_VARIABLE:
						case T_PRIVATE:
						case T_PUBLIC:
						case T_PROTECTED:
						case T_VAR:
						case T_INSTANCEOF:
						case T_STRING:
							$color = "blue";
							break;
						case T_CONSTANT_ENCAPSED_STRING:
							$color = "red";
							break;
						default:
							$color = "black";
							break;
					}

					$highlighted .= $value;
				}

				$end = strlen($highlighted);
				$color_codes[$color][] = $start.":".$end;
			}

			$this->sourcebuffer->set_text($highlighted);

			foreach ($color_codes as $color_code => $positions) {

				foreach ($positions as $position) {

					list($start_pos, $end_pos) = explode(":", $position);
					$start = $this->sourcebuffer->get_iter_at_offset($start_pos);
					$end = $this->sourcebuffer->get_iter_at_offset($end_pos);
					$this->sourcebuffer->apply_tag_by_name($color_code, $start, $end);
				}
			}

		} else {

			$this->sourcebuffer->set_text($filecontents);

		}

	}//protected function highlightSource($filename, $switch)

}//class PHPGTK2Demo extends GtkWindow


$GLOBALS['framework'] = true;
new PHPGTK2Demo();

gtk::main();

?>



