/*****************working**********************/

static PHP_METHOD(GtkAboutDialog, set_documenters)
{
    int len, i=0;
    zval *php_list, **item;
    gchar **list;
    HashTable *args_hash;

    NOT_STATIC_METHOD();

    if (zend_parse_parameters(ZEND_NUM_ARGS() TSRMLS_CC, "a/", 
&php_list) == FAILURE ) {
        return;
    }

    args_hash = HASH_OF(php_list);
    len = zend_hash_num_elements(args_hash);

    list = g_new( gchar *, len ) ;
    zend_hash_internal_pointer_reset(Z_ARRVAL_P(php_list));
    while (zend_hash_get_current_data(Z_ARRVAL_P(php_list), (void  
**)&item) == SUCCESS) {
        convert_to_string_ex( item ) ;
        list[i] = Z_STRVAL_PP( item ) ;
        i++;
        zend_hash_move_forward(Z_ARRVAL_P(php_list)) ;
    }
    list[i]=NULL;
    gtk_about_dialog_set_documenters( 
GTK_ABOUT_DIALOG(PHPG_GOBJECT(this_ptr)), list ) ;
    g_free(list);
}

