<?php
class ToolBar extends GtkToolBar
{
     var $buttons_id = array('gtk-new', 'gtk-media-pause', 
'gtk-refresh', 'gtk-quit');
     var $buttons = array();

     function __construct()
     {
         parent::__construct();
     }

     function load_buttons()
     {
         foreach ($this->buttons_id as $stock_id) {
             $this->buttons[$stock_id] = 
GtkToolButton::new_from_stock($stock_id);
         }
     }

     function add_buttons()
     {
         foreach ($this->buttons as $button) {
             parent::add($button);
         }
     }

     static function get_toolbar()
     {
         $tool = &new ToolBar();
         $tool->load_buttons();
         $tool->add_buttons();
         return $tool;
     }
}
$window = new GtkWindow();
$window->connect_object('destroy', array('gtk','main_quit'));

$toolbar = ToolBar::get_toolbar();

$window->add($toolbar);
$window->show_all();
Gtk::main();
?>

