+%% {{{ GtkTreeStore
+%%
+override gtk_tree_store_new
+PHP_METHOD
+{
+    zval ***args;
+    GType *column_types;
+    GObject *wrapped_obj;
+    int i, argc = ZEND_NUM_ARGS();
+
+    if (argc == 0) {
+        php_error(E_WARNING, "%s::%s() requires at least 1 argument",
+                  get_active_class_name(NULL TSRMLS_CC), \
get_active_function_name(TSRMLS_C)); +        \
PHPG_THROW_CONSTRUCT_EXCEPTION(GtkTreeStore); +    }
+
+    args = php_gtk_func_args(argc);
+
+    column_types = emalloc(argc * sizeof(GType));
+    for (i = 0; i < argc; i++) {
+        column_types[i] = phpg_gtype_from_zval(*args[i]);
+        if (column_types[i] == 0) {
+            efree(column_types);
+            PHPG_THROW_CONSTRUCT_EXCEPTION(GtkTreeStore);
+        }
+    }
+    
+    wrapped_obj = g_object_newv(phpg_gtype_from_zval(this_ptr), 0, NULL);
+    gtk_tree_store_set_column_types(GTK_TREE_STORE(wrapped_obj), argc, \
column_types); +    efree(column_types);
+    efree(args);
+    if (!wrapped_obj) {
+        PHPG_THROW_CONSTRUCT_EXCEPTION(GtkTreeStore);
+    }
+
+    phpg_gobject_set_wrapper(this_ptr, wrapped_obj TSRMLS_CC);
+}
+
+%%
+override gtk_tree_store_append
+PHP_METHOD
+{
+    GtkTreeIter iter;
+    zval *items = NULL;
+    zval *php_parent_iter = NULL;
+    GtkTreeIter *parent_iter = NULL;
+
+    NOT_STATIC_METHOD();
+
+    if (!php_gtk_parse_args(ZEND_NUM_ARGS(), "|Va", &php_parent_iter, \
gtktreeiter_ce, &items)) +        return;
+
+    if (php_parent_iter && Z_TYPE_P(php_parent_iter) != IS_NULL) {
+        if (Z_TYPE_P(php_parent_iter) == IS_OBJECT && \
instanceof_function(Z_OBJCE_P(php_parent_iter), gtktreeiter_ce TSRMLS_CC) && \
phpg_gboxed_check(php_parent_iter, GTK_TYPE_TREE_ITER, FALSE TSRMLS_CC)) { +          \
parent_iter = (GtkTreeIter *) PHPG_GBOXED(php_parent_iter); +        } else {
+            php_error(E_WARNING, "%s::%s() expects parent argument to be a valid \
GtkTreeIter object or null", get_active_class_name(NULL TSRMLS_CC), \
get_active_function_name(TSRMLS_C)); +            return;
+        }
+    }
+
+    gtk_tree_store_append(GTK_TREE_STORE(PHPG_GOBJECT(this_ptr)), &iter, \
parent_iter); +
+    if (items && phpg_model_set_row(GTK_TREE_MODEL(PHPG_GOBJECT(this_ptr)), &iter, \
items) == SUCCESS) +        return;
+
+    phpg_gboxed_new(&return_value, GTK_TYPE_TREE_ITER, &iter, TRUE, TRUE TSRMLS_CC);
+}
+
+%%
+override gtk_tree_store_set
+PHP_METHOD
+{
+    zval ***items;
+    zval *php_iter;
+    GtkTreeIter *iter = NULL;
+    GtkTreeModel *model;
+    int i, argc = ZEND_NUM_ARGS();
+
+    NOT_STATIC_METHOD();
+
+    if (argc < 3) {
+        php_error(E_WARNING, "%s::%s() requires at least 3 arguments, %d given",
+                  get_active_class_name(NULL TSRMLS_CC), \
get_active_function_name(TSRMLS_C), argc); +        return;
+    }
+
+    if (!php_gtk_parse_args(1, "O", &php_iter, gtktreeiter_ce))
+        return;
+
+    iter = (GtkTreeIter *) PHPG_GBOXED(php_iter);
+
+    if ((argc-1) % 2) {
+        php_error(E_WARNING, "%s::%s() requires arguments 2-n to be column/value \
pairs", +                  get_active_class_name(NULL TSRMLS_CC), \
get_active_function_name(TSRMLS_C)); +        return;
+    }
+
+    model = GTK_TREE_MODEL(PHPG_GOBJECT(this_ptr));
+    items = php_gtk_func_args(argc);
+    for (i = 1; i < argc; i += 2) {
+        zval *zcolumn = *items[i];
+        zval *zvalue = *items[i+1];
+        gint column;
+        GValue value = { 0 };
+        
+        if (Z_TYPE_P(zcolumn) != IS_LONG) {
+            php_error(E_WARNING, "%s::%s() requires argument %d to be an integer, %s \
given", +                      get_active_class_name(NULL TSRMLS_CC), \
get_active_function_name(TSRMLS_C), +                      i, \
zend_zval_type_name(zcolumn)); +            efree(items);
+            return;
+        }
+
+        column = Z_LVAL_P(zcolumn);
+        if (column < 0 || column >= gtk_tree_model_get_n_columns(model)) {
+            php_error(E_WARNING, "%s::%s(): argument %d is out of range - model has \
%d column(s)", +                      get_active_class_name(NULL TSRMLS_CC), \
get_active_function_name(TSRMLS_C), +                      i, \
gtk_tree_model_get_n_columns(model)); +            efree(items);
+            return;
+        }
+
+        g_value_init(&value, gtk_tree_model_get_column_type(model, column));
+
+        if (phpg_gvalue_from_zval(&value, zvalue TSRMLS_CC) == FAILURE) {
+            php_error(E_WARNING, "%s::%s(): argument %d is of the wrong type for \
column %d", +                      get_active_class_name(NULL TSRMLS_CC), \
get_active_function_name(TSRMLS_C), +                      i+1, column);
+            efree(items);
+            return;
+        }
+
+        gtk_tree_store_set_value(GTK_TREE_STORE(PHPG_GOBJECT(this_ptr)), iter, \
column, &value); +
+        g_value_unset(&value);
+    }
+
+    efree(items);
+}
+%% }}}
+
 %% {{{ GtkTreeModel

 %%



