["PhpGtkReflector.php.txt" (text/plain)]

<?php
	class PhpGtkReflector extends GtkWindow {
		function __construct() {
			parent::__construct();

			$this->connect_object('destroy', array('gtk', 'main_quit'));
			$this->set_title(__CLASS__);
			$this->set_default_size(400, 300);

			$model = $this->create_model();
			$tree = new GtkTreeView($model);

			$tree->insert_column_with_data_func(-1, "Classes", new GtkCellRendererText, \
array($this, 'col1_setter'));  $cellRenderer = new GtkCellRendererText;
			$cellRenderer->set_property("editable", true);
			$cellRenderer->connect('edited', array($this, 'cellEditDone'), $model);
			$cellRenderer->set_data("column", 1);
			$tree->insert_column_with_data_func(-1, "Info", $cellRenderer, array($this, \
'col2_setter'));

			$scroll = new GtkScrolledWindow();
			$scroll->set_policy(Gtk::POLICY_AUTOMATIC, Gtk::POLICY_AUTOMATIC);
			$scroll->add($tree);

			$this->add($scroll);


			$this->show_all();

		}
		public function cellEditDone($oText, $sPath, $sNewText, $oStore) {
			$iColumn = $oText->get_data("column");
			$oStore->set($oStore->get_iter_from_string($sPath), $iColumn, $sNewText);
		}
		public function col1_setter($column, $cell, $model, $iter) {
			$data = $model->get_value($iter, 0);
			$cell->set_property("text", $data);
		}
		public function col2_setter($column, $cell, $model, $iter) {
			$data = $model->get_value($iter, 1);
			$cell->set_property("text", $data);
		}
		private function create_model() {
			$store = new GtkTreeStore(Gtk::TYPE_STRING, Gtk::TYPE_STRING);

			sort($aClasses = get_declared_classes());
			foreach($aClasses as $sClassName) {
				$oClassIter = $store->append();
				list($iCountProperties, $iCountMethods) = $this->model_add_methods($store, \
$oClassIter, $sClassName);  $store->set($oClassIter, 0, $sClassName, 1, "Properties: \
$iCountProperties, Methods: $iCountMethods");  }
			return $store;
		}
		private function model_add_methods($store, $oParentIter, $sClassName) {
			$oRefClass = new ReflectionClass($sClassName);

			# model properties
			usort($aProperties = $oRefClass->getProperties(), array($this, \
"sortByNameMethod"));  if (($iCountProperties = count($aProperties)) > 0) {
				$oPropsIter = $store->append($oParentIter);
				$store->set($oPropsIter, 0, "Properties");
				foreach ($aProperties as $oRefProp) {
					$oPropIter = $store->append($oPropsIter);
					$store->set($oPropIter, 0, $oRefProp->getName());
				}
			}

			# model methods
			usort($aMethods = $oRefClass->getMethods(), array($this, "sortByNameMethod"));
			if (($iCountMethods = count($aMethods)) > 0) {
				$oMethodsIter = $store->append($oParentIter);
				$store->set($oMethodsIter, 0, "Methods");
				foreach ($aMethods as $oRefMethod) {
					$oMethodIter = $store->append($oMethodsIter);
					$store->set($oMethodIter, 0, $oRefMethod->getName());
				}
			}

			return array($iCountProperties, $iCountMethods);
		}
		private function sortByNameMethod($a, $b) {
			return strcmp($a->getName(), $b->getName());
		}
	}

	new PhpGtkReflector;
	Gtk::main();
?>



-- 
