        $notebook->append_page(new GtkLabel('Page2'), new
GtkLabel('Label2'));
        $notebook->connect('switch-page', array($this,
'onSwitchNotebookPage'));
        $this->show_all();
    }

    function onSwitchNotebookPage(GtkNotebook $notebook) {
        echo 'switch' . "\r\n";
    }
}

new Notebooktest();
