Sorry. The attachment got stripped out. Here is the code:

<?php
if (!extension_loaded('gtk')) {
   dl( 'php_gtk2.' . PHP_SHLIB_SUFFIX);
}

class TestClass {
   public function __construct() {
     throw new Exception('TestClass __construct problem.');
   }
}

function func0($param) {
   try {
     $menuItem = new GtkMenuItem('GtkMenuItem');
   } catch (Exception $e) {
     echo $e->getMessage() . "\n";
     echo $e->getTraceAsString() . "\n";
   }

   try {
     $testClass = new TestClass('TestClass');
   } catch (Exception $e) {
     echo $e->getMessage() . "\n";
     echo $e->getTraceAsString() . "\n";
   }
}

func0('func0');
?>

