    {
        $this->model = new GtkListStore(Gtk::TYPE_STRING, Gtk::TYPE_STRING, \
Gtk::TYPE_STRING, Gtk::TYPE_STRING);  $this->model->append(array(1, 'aaaa', 'aaaa', \
'aaaa'));  $this->model->append(array(2, 'bbbb', 'bbbb', 'bbbb'));
	$this->model->append(array(3, 'cccc', 'cccc', 'cccc'));
	$this->model->append(array(4, 'dddd', 'dddd', 'dddd'));


	$iter = $this->model->get_iter_first();
	var_dump($iter);

        $nome = $this->model->get_value($iter, 1);
        var_dump($nome);

        while ($iter = $this->model->iter_next($iter))
        {
            var_dump($iter);
            $nome = $this->model->get_value($iter, 1);
            var_dump($nome);
        }
    }
}

