+%% {{{ GtkCalendar
+
+%%
+override gtk_calendar_get_date 
+static PHP_METHOD(GtkCalendar, get_date)
+{
+  guint year, month, day;
+  char date[11];
+  
+  NOT_STATIC_METHOD();
+  if (!php_gtk_parse_args(ZEND_NUM_ARGS(), ""))
+    return;
+  
+  array_init(return_value);
+  
+  gtk_calendar_get_date(GTK_CALENDAR(PHPG_GOBJECT(this_ptr)), &year, &month, &day);
+ 
+  snprintf(date, sizeof(date), "%04d-%02d-%02d", year, month+1, day);
+  
+  add_index_long(return_value, 0, year);
+  add_index_long(return_value, 1, month);
+  add_index_long(return_value, 2, day);
+  add_index_string(return_value, 3, date, 1);
+}
+
