<?php
//file: plugin.php

if( $GLOBALS['argc'] !== 2) {
        //check to see if winXID was passed
        die ("\r\n! Please pass the Window XID you wish to plug (try: xwininfo
-int) !\r\n\r\n");      
}

$win = new GtkWindow();
$win->set_position(Gtk::WIN_POS_CENTER);
$win->set_default_size(640, 480);
$win->connect_simple('destroy', array('gtk', 'main_quit'));
$win->set_title('you complete me, if you can...');

//setup the socket
  $sock = new GtkSocket();
  $win->add($sock);
  $sock->realize();
  $sock_id = $sock->$GLOBALS['argv'][1];

//use the window xid passed in
  $plug = new GtkPlug($GLOBALS['argv'][1]);
  $plug->construct($sock_id);
  $plug->show_all();

$win->show_all();
Gtk::main();
?>

