+add-arginfo GdkPixbuf add_alpha
+static
+ZEND_BEGIN_ARG_INFO_EX(ARGINFO_NAME, 0, 0, 3)
+    ZEND_ARG_INFO(0, substitute_or_red)
+    ZEND_ARG_INFO(0, green)
+    ZEND_ARG_INFO(0, blue)
+ZEND_END_ARG_INFO();
+
+%%
+override gdk_pixbuf_add_alpha
+PHP_METHOD
+{
+	zend_bool substitute = TRUE;
+	int red, blue, green;
+	guchar r = 255, g = 255, b = 255;
+	GdkPixbuf* pixbuf;
+
+	NOT_STATIC_METHOD();
+
+	if (ZEND_NUM_ARGS() > 1) {
+		if (!php_gtk_parse_args(ZEND_NUM_ARGS(), "iii", &red, &green, &blue)) {
+			return;
+		} else {
+			r = (guchar)red;
+			g = (guchar)green;
+			b = (guchar)blue;
+		}
+	} else {
+		if (!php_gtk_parse_args(ZEND_NUM_ARGS(), "|b", &substitute))
+			return;
+	}
+
+	pixbuf = gdk_pixbuf_add_alpha(GDK_PIXBUF(PHPG_GOBJECT(this_ptr)), (gboolean)substitute, r, g, b);
+
+	if (pixbuf) {
+		phpg_gobject_new(&return_value, (GObject *)pixbuf TSRMLS_CC);
+		g_object_unref(pixbuf);
+	}
+}
