+/* {{{ Pointer_Arg */
+class Pointer_Arg extends Arg_Type {
+
+    static $pointer_tpl = "\n\t%(name) = %(class)(PHPG_GPOINTER(php_%(name)));\n";
+    var $type           = null;
+    var $typecode       = null;
+
+    function __construct($type, $typecode)
+    {
+        $this->type     = $type;
+        $this->typecode = $typecode;
+    }
+
+    function write_param($type, $name, $default, $null_ok, $info)
+    {
+        $indir = strstr($type, '*');
+        $type = str_replace($indir, '', $type);
+        $ce = strtolower($type."_ce");
+        $class = str_replace('TYPE_', '', $this->typecode);
+        $info->var_list->add($type, $indir.$name);
+        $info->var_list->add('zval', '*php_'.$name." = NULL");
+        $info->arg_list[] = $name;
+        $info->add_parse_list('N', array('&php_' . $name, $ce));
+        $info->pre_code[] = aprintf(self::$pointer_tpl,
+                                    array('name' => $name,
+                                          'class' => $class,
+                                          'name' => $name));
+    }
+
+    function write_return($type, $owns_return, $info)
+    {
+        if (substr($type, -1) == '*') {
+            $info->var_list->add($this->type, '*php_retval');
+            $ret = 'php_retval';
+        } else {
+            $info->var_list->add($this->type, 'php_retval');
+            $ret = '&php_retval';
+        }
+
+        $info->post_code[] = sprintf("\tphpg_gpointer_new(&return_value, %s, %s \
TSRMLS_CC);\n", +                                     $this->typecode, $ret);
+    }
+}
