I have write an overrid for GtkCalendar::get_date()

%%
override gtk_calendar_get_date
PHP_METHOD
{
    guint year;
    guint month;
    guint day;
    zval *zyear;
    zval *zmonth;
    zval *zday;


    NOT_STATIC_METHOD();


gtk_calendar_get_date(GTK_CALENDAR(PHPG_GOBJECT(this_ptr)),&year,&month,&day);

    if (ZEND_NUM_ARGS() == 3) {
        if (zend_parse_parameters(ZEND_NUM_ARGS() TSRMLS_CC, "zzz", 
&zyear, &zmonth, &zday) == FAILURE) {
            return;
        }
        zval_add_ref(&zyear);
        zval_add_ref(&zmonth);
        zval_add_ref(&zday);
        ZVAL_LONG(zyear, year);
        ZVAL_LONG(zmonth, month);
        ZVAL_LONG(zday, day);
    }
    else {
        if(!php_gtk_parse_args(ZEND_NUM_ARGS(), "")) {
            return;
        }
    }
    array_init(return_value);

    add_next_index_long(return_value, year);
    add_next_index_long(return_value, month);
    add_next_index_long(return_value, day);
}


