+        for (i=0, zend_hash_internal_pointer_reset(Z_ARRVAL_P(arr));
+             zend_hash_get_current_data(Z_ARRVAL_P(arr), (void **)&data) == SUCCESS;
+             zend_hash_move_forward(Z_ARRVAL_P(arr))) {
+             
