+                artists[i++] = Z_STRVAL_PP(data);
+
+        }
+        
+        artists[i] = NULL;
+        gtk_about_dialog_set_artists(GTK_ABOUT_DIALOG(PHPG_GOBJECT(this_ptr)), \
(const gchar **)artists); +        efree(artists);
+	
