This is a short version.
if i set the depth of $this->pixmaps to 1, php-gtk crash/ends.
I require depth 1 because i willl use shape_combine_mask for transparent 
background.
I have test it with "gtk::events_pending" and with "gtk::main()", both 
have the same result.

<?PHP
   dl("php_gtk2.so");
   error_reporting(E_ALL | E_STRICT);
   set_time_limit(0);
   ob_implicit_flush(1);

   function ticks() {
      $res = debug_backtrace();
      if(@isset($res[1]))
         $cur = $res[1];
      else
         $cur = $res[0];
      if(!@isset($cur["file"]) || !@isset($cur["line"])) {
         $cur["file"] = "??? ".$res[0]["file"];
         $cur["line"] = "??? ".$res[0]["line"];
      }
      else {
         $cur["line"] .= " => ".$res[0]["line"];
      }
      echo $cur["file"]."(".$cur["line"].")";
      if(@isset($cur["class"])) {
         echo $cur["class"].$cur["type"].$cur["function"]."(";
         $args = "";
         for($i=0;$i<count($cur["args"]);$i++) {
            if($args != "") $args .= ",";
            if(is_string($cur["args"][$i]))
               $args .= "\"".addslashes($cur["args"][$i])."\"";
            else if(is_object($cur["args"][$i]))
                $args .= "(object)".get_class($cur["args"][$i]);
            else
               $args .= $cur["args"][$i];
         }
         echo $args.")";
      }
      else if($cur["function"] != "ticks") {
         echo $cur["function"];
      }
//      while(gtk::events_pending()) {gtk::main_iteration_do(1);}
      echo "\r\n";
   }

   register_tick_function("ticks");
   declare(ticks=1);

   $break_loop = 0;

   class Fun extends GtkWindow {
      protected $da;
      protected $pixmap;
      protected $pixmaps;

      protected $width;
      protected $height;

      function __construct($parent = null) {
         parent::__construct();
         $this->width  = (int)(gdk::screen_width()/4);
         $this->height = (int)(gdk::screen_height()/4);

         $this->connect_simple('destroy', array(&$this, 'quit'));
         $this->set_default_size($this->width, $this->height);

         $this->__create_da();
         $this->show_all();

         mt_srand((double)microtime());
         gtk::timeout_add(1,array($this,"TexUpdate"));
      }

      function __create_da() {
         $this->da = new GtkDrawingArea();
         $this->da->set_size_request($this->width, $this->height);
         $this->add($this->da);
         $this->da->realize();
         $this->da->connect('configure-event', array(&$this, 
'configure_event'));
      }

      function quit() {
         global $break_loop;
         echo "destroy <br />\r\n";
         $this->destroy();
         $break_loop = 1;
         gtk::main_quit();
      }

      function configure_event($widget, $event) {
// Only depth == 1
         $this->pixmaps = new GdkPixmap($widget->window,
                                        $this->width,
                                        $this->height,
                                        1);
         return true;
      }

      function do_loop() {
         if(!function_exists("ticks"));
            while(gtk::events_pending()) {gtk::main_iteration_do(0);}
      }

      function TexUpdate() {
// Here is an BUG ????
         $this->pixmaps->draw_rectangle($this->style->white_gc,
                                        true, 10, 10,
                                        2,
                                        2);
      }
   };

   $fun = new Fun();

   gtk::main();

/*
   while(!$break_loop) {
      $fun->do_loop();
      if(!$break_loop) {
         $fun->TexUpdate(100,100);
      }
   }
*/
?>

