>>>>            $this->tex_data[$dmx][$dmy]=$val;
>>>>         }
>>>>         if(@isset($this->tex_data[$dmx][$dmy]))
>>>>            return($this->tex_data[$dmx][$dmy]);
>>>>         else
>>>>            return(0);
>>>>      }
>>>>
>>>>      function TexMake() {
>>>>         global $break_loop;
>>>>         $width  = $this->width;
>>>>         $height = $this->height;
>>>>         $mx = &$this->mx;
>>>>         $my = &$this->my;
>>>>         $tex_data = &$this->tex_data;
>>>>
>>>>
>>>>         if($this->done) {
>>>>            $this->TexReset();
>>>>         }
>>>>
>>>>         // Check To Make Sure We Are Not Trapped (Nowhere Else  To  
>>>> Move)
>>>>         /*
>>>>         if((($mx>($width-4) || $this->tex($mx+2,$my)==255)) &&
>>>>            (($mx<2 || $this->tex($mx-2,$my)==255)) &&
>>>>            (($my>($height-4) || $this->tex($mx,$my+2)==255)) &&
>>>>            (($my<2 || $this->tex($mx,$my-2)==255))) {
>>>>            do  {                                                  
>>>> //  If We Are  Trapped
>>>>               $mx=(int)(rand()%($width/2)) *2;                    
>>>> //  Pick A New Random X Position
>>>>               $my=(int)(rand()%($height/2)) *2;                   
>>>> //  Pick A New Random Y Position
>>>>               $this->do_loop();
>>>>            } while(!$break_loop && $this->tex($mx,$my) ==0);      
>>>> //  Keep Picking A Random Position Until We Find
>>>>         }                                                        
>>>> //   One That Has Already Been Tagged (Safe Starting Point)
>>>>         */
>>>>
>>>>         static $no_found = 0;
>>>>         {
>>>>
>>>>            $dir=(int)mt_rand(1,8)% 4;                             
>>>> //  Pick A Random Direction
>>>>            if(($dir==0) && ($mx<=($width-4)))  {                  
>>>> //  If The Direction Is 0 (Right) And We Are  Not At The Far Right
>>>>               if($this->tex($mx+2,$my)==0)  {                     
>>>> //  And If The Room To The Right Has Not  Already Been Visited
>>>>                  $this->TexUpdate($mx+1, $my);                    
>>>> //  Update The Texture To Show Path Cut  Out Between Rooms
>>>>                  $mx +=2;                                         
>>>> //  Move To The  Right (Room To The Right)
>>>>                  $no_found = 0;
>>>>               }
>>>>               else {
>>>>                  $no_found++;
>>>>               }
>>>>            }
>>>>            else if(($dir==1) && ($my<=($height-4)))  {            
>>>> //  If The Direction Is 1 (Down) And We Are Not At  The Bottom
>>>>               if($this->tex($mx,$my+2)==0)  {                     
>>>> //  And If The Room Below Has Not Already  Been Visited
>>>>                  $this->TexUpdate($mx,$my +1);                    
>>>> //  Update The Texture To Show Path Cut  Out Between Rooms
>>>>                  $my +=2;                                         
>>>> //  Move Down (Room  Below)
>>>>                  $no_found = 0;
>>>>               }
>>>>               else {
>>>>                  $no_found++;
>>>>               }
>>>>            }
>>>>            else if(($dir==2) && ($mx>=2))  {                      
>>>> //  If The Direction Is 2 (Left) And We  Are Not At The Far Left
>>>>               if($this->tex($mx-2,$my)==0)  {                     
>>>> //  And If The Room To The Left Has Not  Already Been Visited
>>>>                  $this->TexUpdate($mx-1, $my);                    
>>>> //  Update The Texture To Show Path Cut  Out Between Rooms
>>>>                  $mx- =2;                                         
>>>> //  Move To The Left  (Room To The Left)
>>>>                  $no_found = 0;
>>>>               }
>>>>               else {
>>>>                  $no_found++;
>>>>               }
>>>>            }
>>>>            else if(($dir==3) && ($my>=2))  {                      
>>>> //  If The Direction Is 3 (Up) And We Are  Not At The Top
>>>>               if($this->tex($mx,$my-2)==0)  {                     
>>>> //  And If The Room Above Has Not Already  Been Visited
>>>>                  $this->TexUpdate($mx, $my-1);                    
>>>> //  Update The Texture To Show Path  Cut Out Between Rooms
>>>>                  $my- =2;                                         
>>>> //  Move Up (Room Above)
>>>>                  $no_found = 0;
>>>>               }
>>>>               else {
>>>>                  $no_found++;
>>>>               }
>>>>            }
>>>>            if($no_found >= 20) {
>>>>               $i = 0;
>>>>               $no_found = 0;
>>>>               while(!$break_loop && $this->tex($mx,$my)==255)   
>>>> {                                               // If We Are Trapped
>>>>                  $i++;
>>>> /*
>>>>                  $mx=(int)(rand()%($width/2)) *2;                 
>>>> //  Pick A New Random X Position
>>>>                  $my=(int)(rand()%($height/2)) *2;                
>>>> //  Pick A New Random Y Position
>>>> */
>>>>                  if($i > 100 && !($i%1000)) {
>>>>                     if(!($i%1000)) {
>>>>                        if($this->get_done()) {
>>>>                           return;
>>>>                        }
>>>>                        $this->mx = $this->mx_free;
>>>>                        $this->my = $this->my_free;
>>>>                        break;
>>>>                     }
>>>>                  }
>>>>               };                   // Keep Picking A Random   
>>>> Position Until We Find
>>>>            }
>>>>         }
>>>>
>>>>
>>>>         $this->TexUpdate($mx,$my);
>>>>         $this->gen_shape();
>>>>      }
>>>>
>>>>      function get_done_A() {
>>>>         for($x=$this->mx;$x<$this->width;$x+=2)   
>>>> {                   // Loop Through All The Rooms
>>>>            for($y=$this->my;$y<$this->height;$y+=2)   
>>>> {               // On X And Y Axis
>>>>               if($this->tex($x,$y)==0)   
>>>> {                            // If Current Texture Pixel (Room)  
>>>> Is  Blank
>>>>                  $this->mx_free = $x;
>>>>                  $this->my_free = $y;
>>>>                  return  
>>>> (false);                                     // We Have To Set  
>>>> done  To False (Not Finished Yet)
>>>>               }
>>>>            }
>>>>         }
>>>>         return(true);
>>>>      }
>>>>
>>>>      function get_done_B() {
>>>>         for($x=$this->mx-2;$x>=0;$x-=2)   
>>>> {                           // Loop Through All The Rooms
>>>>            for($y=$this->my-2;$y>=0;$y-=2)   
>>>> {                        // On X And Y Axis
>>>>               if($this->tex($x,$y)==0)   
>>>> {                            // If Current Texture Pixel (Room)  
>>>> Is  Blank
>>>>                  $this->mx_free = $x;
>>>>                  $this->my_free = $y;
>>>>                  return  
>>>> (false);                                     // We Have To Set  
>>>> done  To False (Not Finished Yet)
>>>>               }
>>>>            }
>>>>         }
>>>>         return(true);
>>>>      }
>>>>
>>>>      function get_done_C() {
>>>>         for($x=0;$x<$this->width;$x+=2)   
>>>> {                           // Loop Through All The Rooms
>>>>            for($y=0;$y<$this->height;$y+=2)   
>>>> {                       // On X And Y Axis
>>>>               if($this->tex($x,$y)==0)   
>>>> {                            // If Current Texture Pixel (Room)  
>>>> Is  Blank
>>>>                  $this->mx_free = $x;
>>>>                  $this->my_free = $y;
>>>>                  return  
>>>> (false);                                     // We Have To Set  
>>>> done  To False (Not Finished Yet)
>>>>               }
>>>>            }
>>>>         }
>>>>         return(true);
>>>>      }
>>>>
>>>>      function get_done() {
>>>>         static $c = 0;
>>>>         if(!$c) {
>>>>            $c = 1;
>>>>            if($this->get_done_A() && $this->get_done_B() &&  $this- 
>>>> >get_done_C()) {
>>>>               $this->done = true;
>>>>               return(true);
>>>>            }
>>>>         }
>>>>         else {
>>>>            $c = 0;
>>>>            if($this->get_done_B() && $this->get_done_A() &&  $this- 
>>>> >get_done_C()) {
>>>>               $this->done = true;
>>>>               return(true);
>>>>            }
>>>>         }
>>>>         return(false);
>>>>      }
>>>>   };
>>>>
>>>>   $fun = new Fun();
>>>>
>>>>   while(!$break_loop) {
>>>>      $fun->do_loop();
>>>>      if(!$break_loop) {
>>>>         $fun->TexMake();
>>>>      }
>>>>   }
>>>> ?>
>>>>
