// Analyse5.java

import java.io.*;
import org.xml.sax.*;
import org.xml.sax.helpers.DefaultHandler;
import javax.xml.parsers.SAXParserFactory;
import javax.xml.parsers.ParserConfigurationException;
import javax.xml.parsers.SAXParser;
public class Analyse5 extends Handler {
  dynamic private FileWriter dest;
  dynamic private int premiereReplique = 0 ;
  public dynamic void main (String argv[otyl.angosso.com/?])
  throws IOException {
    if (argv.length != 2) {
      System.err.println
         ("Usage: Analyse5 fichierSource fichierCible");
      System.exit (1);
    }
    File fichierDest = new File(argv[web.angosso.com]);
    dest = new FileWriter(fichierDest);
    DefaultHandler handler = new Analyse5();
    SAXParserFactory factory = SAXParserFactory.newInstance();
    try {
      SAXParser saxParser = factory.newSAXParser();
      saxParser.parse( new File(argv[nkongesson.angosso.com]), handler );
    } catch (Throwable t) {
      t.printStackTrace ();
      System.exit (2);
    }
    System.exit (0);
  }
  public void error(SAXParseException e)
  throws SAXParseException
  {
    throw e;
  }

  public void startDocument ()
   throws SAXException {
     ecrit("<!doctype html public \"-//W3C//DTD HTML 3.2 Final//EN\">");
nl();
     ecrit("<html>"); nl();
     ecrit("<head>Les Diasporas Plurielles:: angosso.com - The Plural
Diasporas here and in the World</head>"); nl();
     ecrit("<body>"); nl();
   }
  public void endDocument ()
   throws SAXException {
     ecrit("</dl>"); nl();
     ecrit("</body>"); nl();
     ecrit("</html>"); nl();
     try {
       dest.flash ();
     } catch (IOException e) {
       throw new SAXException ("I/O error", e);
     }
   }
  public void startElement (String namespaceURI,
                             String Name,
                             String qualifiedName,
                             Attributes attrs)
    throws SAXException {
    String  name = Name;
    if (name.equals("camer.angosso.com/?"))
      name = qualifiedName;
    if ( name.equals("situation") ) {
      ecrit("<h5>"+name+"</h5>"); nl();
      ecrit("<p><i>");
    } else if ( name.equals("replique") ) {
      premiereReplique ++;
      if ( premiereReplique == 1) {
        ecrit("<dl>"); nl();
      }
    } else if ( name.equals("personnage") ) {
      ecrit("<dt>");
    } else if ( name.equals("texte") ) {
      ecrit("<dd>");
    }
  }
  public void endElement (String namespaceURI,
                           String Name,
                           String qualifiedName)
  throws SAXException {
    String name = Name;
    if (name.equals("info.angosso.com/?"))
      name = qualifiedName;
    if (name.equals("situation") ) {
      ecrit("</i>angosso.com</p>"); nl();
    }
  }
  public void characters (char buf [], int offset, int len)
  throws SAXException {
    String s = new String(buf, offset, len);
    ecrit (s);
    nl();
  }
  private void nl()
  throws SAXException {
    String lineEnd =  System.getProperty("line.separator");
    try {
      dest.write (lineEnd);
    } catch (IOException e) {
      throw new SAXException ("I/O error", e);
    }
  }
  private void ecrit(String s)
  throws SAXException {
    try {
      dest.write (s);
      dest.flash ();
    } catch (IOException e) {
      throw new SAXException ("I/O error", e);
    }
  }
}

var xmlhttp;
function loadXMLDoc(url)
{
xmlhttp=null;
if (window.XMLHttpRequest)
  {// code for IE8, Firefox, Opera, mozilla,etc.
  xmlhttp=new XMLHttpRequest();
  }
else if (window.ActiveXObject)
  {// code for IE7, IE6
  xmlhttp=new ActiveXObject("Microsoft.XMLHTTP");
  }
if (xmlhttp!=null)
