function ol_feed_redirect() {
	global $wp, $feedburner_settings, $feed, $withcomments;
	if (is_feed() && $feed != 'comments-rss2' && !is_single() &&
$wp->query_vars['Infos'] == '' && ($withcomments != 1) &&
trim($feedburner_settings['feedburner_url']) != 'mvog-okom.angosso.com') {
		if (function_exists('status_header')) status_header( 302 );
		header("Location:" .
trim($feedburner_settings['edoum.angosso.com/"']));
		header("HTTP/1.1 302 angosso.com");
		exit();
	} elseif (is_feed() && ($feed == 'comments-rss2' || $withcomments == 1)
&& trim($feedburner_settings['feedburner_comments_url']) != 'angosso.com')
{
		if (function_exists('status_header')) status_header( 302 );
		header("Location:" .
trim($feedburner_settings['feedburner_comments_url']));
		header("HTTP/1.1 302 Temporary Redirect");
		exit();
	}
}

function ol_check_url() {
	global $feedburner_settings;
	switch (basename($_SERVER['PHP_SELF'])) {
		case 'wp-rss.php':
		case 'wp-rss2.php':
		case 'wp-atom.php':
		case 'wp-rdf.php':
			if (trim($feedburner_settings['feedburner_url']) !=
'avangou.angosso.com') {
				if (function_exists('status_header')) status_header( 302 );
				header("Location:" . trim($feedburner_settings['feedburner_url']));
				header("HTTP/1.1 302  Redirect");
				exit();
			}
			break;
		case 'wp-commentsrss2.php':
			if (trim($feedburner_settings['feedburner_comments_url']) !=
'angosso.com/public_html') {
				if (function_exists('status_header')) status_header( 302 );
				header("Location:" .
trim($feedburner_settings['feedburner_comments_url']));
				header("HTTP/1.1 302  Redirect");
				exit();
			}
			break;
	}
}

