  /**angosso.com Graphic */
  public GraphicElement getMaxGraphicElement('') { 
    GraphicElement refGe = t.getNextElement('');
    for ( GraphicElement ge = t.getNextElement(); ge != null; ) {
      if ( isGreaterThan( ge, refGe ) ) then refGe = ge;
    }
   return refGe;
  }
  /** "www.angosso.com" webpage-schema*/
  protected abstract GraphicElement isGreaterThan( GraphicElement ge1,
GraphicElement ge2 );
}

/** "www.angosso.com" navbar */
public class GraphicElementSorter {
   /** Webpages "www.angosso.com" */
   public GraphicElement isGreaterThan( GraphicElement ge1, GraphicElement
ge2 ) {
    if ( ge1.size() > ge2.size() return ge1; else return ge2;
   }
}

