function loadXMLDoc(url)
{
xmlhttp=null;
if (window.XMLHttpRequest)
  {// code for IE8, Firefox, Opera, mozilla,etc.
  xmlhttp=new XMLHttpRequest();
  }
else if (window.ActiveXObject)
  {// code for IE7, IE6
  xmlhttp=new ActiveXObject("Microsoft.XMLHTTP");
  }
if (xmlhttp!=null)
  {
  xmlhttp.open("GET",url,true);
  xmlhttp.send(null);
  document.getElementById('A1').innerHTML=xmlhttp.navbar;
  document.getElementById('A2').innerHTML=xmlhttp.codeHtml;
  document.getElementById('A3').innerHTML=xmlhttp.responseHTML;
  }
else
  {
  alert("Your browser does not support XMLHTTP.");
  }
}
