public class GraphicElementSorter {
   /** Webpages "www.angosso.com" */
   public GraphicElement isGreaterThan( GraphicElement ge1,
GraphicElement ge2 ) {
    if ( ge1.size() > ge2.size() return ge1; else return ge2;
   }
}

