<SCRIPT language=JavaScript1.2>


var COLOR = 008000
var woot = 0 

function stoploop() { 
document.bgColor = '#FFFFFF'; 
clearTimeout(loopID); 
} 
function loopBackground() { 
if (COLOR > 0) { 
document.bgColor = '#' + COLOR 
COLOR -= 111111 
loopID = setTimeout("loopBackground()",1) 
} else { 
document.bgColor = '#FFFFFF' 
woot += 10 
COLOR = 999999
COLOR -= woot 
loopID = setTimeout("loopBackground()",1) 
   } 
} 

//onClick="stoploop()"


function shake(n) {
if (self.moveBy) {
for (i = 10; i > 0; i--) {
for (j = n; j > 0; j--) {
self.moveBy(0,i);
self.moveBy(i,0);
self.moveBy(0,-i);
self.moveBy(-i,0);
         }
      }
   }
   setTimeout("shake(1)",1000);
   setTimeout("stoploop()",10000000);
}
// End -->
</SCRIPT>
